import React, { Component } from "react";
import Result from "./results";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  // Form,
  // FormGroup,
  Input,
  Label,
} from "reactstrap";

export default class CatchPokemon extends Component {
  static defaultProps =  {
    secret : Math.floor(Math.random() * 10) + 1
  }
  constructor(props) {
    super(props);
    this.state = {
      activePokemon: this.props.activePokemon,
      term: '',
    };
    this.handleChange = this.handleChange.bind(this)
  }

  handleChange(event) {
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  render() {
    const { toggle, onSave, word } = this.props;

    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}>Pokemon</ModalHeader>
        <ModalBody>
          <p>Name: {this.state.activePokemon.name}</p>
          <p>Level: {this.state.activePokemon.level}</p>
          <p>Hp: {this.state.activePokemon.hp}</p>
          <p>Attack: {this.state.activePokemon.attack}</p>
          <p>Defense: {this.state.activePokemon.defense}</p>
		      <p>Element: {this.state.activePokemon.element}</p>
		      <p>Captured before: {(this.state.activePokemon.owned === true) ? "Yes" : "No"}</p>
          <Label htmlFor='term'>Guess the number to (1-10)</Label>
          <Input
            type='text'
            id='term'
            name='term'
            value={this.state.term}
            onChange={this.handleChange}
          />
          <Result 
            term={this.state.term}
            secretNum={this.props.secret}
          />
        </ModalBody>
        <ModalFooter>
        {(this.props.tern === this.props.secret) ? (
        console.log('yes')
        ) : <p>nope</p> }
        </ModalFooter>
      </Modal>
    );
  }
}
