import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  // Form,
  // FormGroup,
  // Input,
  // Label,
} from "reactstrap";

export default class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activePokemon: this.props.activePokemon,
    };
  }

  // handleChange = (e) => {
  //   let { name, value } = e.target;

