  //   this.setState({ activePokemon })
  // };

  render() {
    const { toggle, onSave } = this.props;

    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}>Pokemon</ModalHeader>
        <ModalBody>
          <p>Name: {this.state.activePokemon.name}</p>
          <p>Level: {this.state.activePokemon.level}</p>
          <p>Hp: {this.state.activePokemon.hp}</p>
          <p>Attack: {this.state.activePokemon.attack}</p>
          <p>Defense: {this.state.activePokemon.defense}</p>
          {/* <Form> */}
            {/* <FormGroup>
              <Label for="pokemon-name">Name</Label>
              <Input
                type="text"
                id="pokemon-name"
                name="name"
                value={this.state.activePokemon.name}
                onChange={this.handleChange}
                placeholder="Enter Pokemon Name"
              />
            </FormGroup> */}
            <p>Element: {this.state.activePokemon.element}</p>
            {/* <FormGroup>
              <Label for="pokemon-element">Element</Label>
              <Input
                type="text"
                id="pokemon-element"
                name="element"
                value={this.state.activePokemon.element}
                onChange={this.handleChange}
                placeholder="Enter Pokemon Element"
              />
            </FormGroup> */}
            <p>Captured: {(this.state.activePokemon.owned === true) ? "Yes" : "No"}</p>
            {/* <FormGroup check>
              <Label check>
                <Input
                  type="checkbox"
                  name="owned"
                  checked={this.state.activePokemon.owned}
                  onChange={this.handleChange}
                />
                Owned
              </Label>
            </FormGroup> */}
          {/* </Form> */}
        </ModalBody>
        <ModalFooter>
          <Button
            color="success"
            onClick={() => onSave(this.state.activePokemon)}
          >
            Return
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
