import React, { Component } from "react";
import Modal from "./components/Modal";
import axios from "axios";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewOwned: false,
      pokemonList: [],
      modal: false,
      activePokemon: {
        name: "",
        element: "",
        owned: false,
      },
    };
  }

  componentDidMount() {
    this.refreshList();
  }

  refreshList = () => {
    axios
      .get("/api/pokemons/")
      .then((res) => this.setState({ pokemonList: res.data }))
      .catch((err) => console.log(err));
  };

  toggle = () => {
    this.setState({ modal: !this.state.modal });
  };

  handleSubmit = (pokemon) => {
    this.toggle();

    if (pokemon.id) {
      axios
        .put(`http://localhost:8000/api/pokemons/${pokemon.id}/`, pokemon)
        .then((res) => this.refreshList());
      return;
    }
    axios
      .post("http://localhost:8000/api/pokemons/", pokemon)
      .then((res) => this.refreshList());
  };

  handleDelete = (pokemon) => {
    axios
      .delete(`http://localhost:8000/api/pokemons/${pokemon.id}/`)
      .then((res) => this.refreshList());
  };

  createPokemon = () => {
    const pokemon = { name: "", element: "", owned: false };

    this.setState({ activePokemon: pokemon, modal: !this.state.modal });
  };

  editPokemon = (pokemon) => {
    this.setState({ activePokemon: pokemon, modal: !this.state.modal });
  };

  displayOwned = (status) => {
    if (status) {
      return this.setState({ viewOwned: true });
    }

    return this.setState({ viewOwned: false });
  };

  renderTabList = () => {
    return (
      <div className="nav nav-tabs">
        <span
          onClick={() => this.displayOwned(true)}
          className={this.state.viewOwned ? "nav-link active" : "nav-link"}
        >
          Owned
        </span>
        <span
          onClick={() => this.displayOwned(false)}
          className={this.state.viewOwned ? "nav-link" : "nav-link active"}
        >
          Unowned
        </span>
      </div>
    );
  };

  renderItems = () => {
    const { viewOwned } = this.state;
    const newPokemons = this.state.pokemonList.filter(
      (pokemon) => pokemon.owned === viewOwned
    );

    return newPokemons.map((pokemon) => (
      <li
        key={pokemon.id}
        className="list-group-item d-flex justify-content-between align-items-center"
      >
        <span
          className={`pokemon-name mr-2 ${
            this.state.viewOwned ? "owned-pokemon" : ""
          }`}
          name={pokemon.name}
        >
          {pokemon.name}
        </span>
        <span>
          <button
            className="btn btn-info me-2"
            onClick={() => this.editPokemon(item)}
          >
            Edit
          </button>
          <button
            className="btn btn-danger"
            onClick={() => this.handleDelete(item)}
          >
            Delete
          </button>
        </span>
      </li>
    ));
  };

  render() {
    return (
      <main className="container">
        <h1 className="text-black text-uppercase text-center my-4">Pokemon</h1>
        <div className="row">
          <div className="col-md-6 col-sma-10 mx-auto p-0">
            <div className="card p-3">
              <div className="mb-4">
                <button className="btn btn-warning" onClick={this.createItem}>
                  Add task
                </button>
              </div>
              {this.renderTabList()}
              <ul className="list-group list-group-flush border-top-0">
                {this.renderItems()}
              </ul>
            </div>
          </div>
        </div>
        {this.state.modal ? (
          <Modal
            activeItem={this.state.activeItem}
            toggle={this.toggle}
            onSave={this.handleSubmit}
          />
        ) : null}
      </main>
    );
  }
}

export default App;
