import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label,
} from "reactstrap";

export default class CatchPokemon extends Component {
  static defaultProps =  {
    secret : Math.floor(Math.random() * 20) + 1
  }
  constructor(props) {
    super(props);
    this.state = {
      activePokemon: this.props.activePokemon,
	    random: 5,
	    guess: 0,
	    match: false,
      term: '',
    };
    this.handleChange2 = this.handleChange2.bind(this)
  }

  handleChange2(event) {
    this.setState({
      [event.target.name] : event.target.value
    })
  }

  handleChange = (e) => {
    const { value } = e.target;
	this.setState({ guess: value })
	this.checkMatch();
  };

  // checkMatch = () => {
	// const guess = this.state.guess
	// const random = this.state.random
	// if (guess === random) {
	// 	this.setState({ match: true});
	// 	console.log(guess);
	// 	console.log(random);
	// } else {
	// 	this.setState({ match: false});
	// 	console.log(guess);
	// 	console.log(random);
	// }
  // }

  generateNumber = () => {
	const randomNumber = Math.floor(Math.random() * 10) + 1;
	this.setState({ guess: randomNumber })
  }

  render() {
    const { toggle, onSave, word } = this.props;

    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}>Pokemon</ModalHeader>
        <ModalBody>
          <p>Name: {this.state.activePokemon.name}</p>
          <p>Level: {this.state.activePokemon.level}</p>
          <p>Hp: {this.state.activePokemon.hp}</p>
          <p>Attack: {this.state.activePokemon.attack}</p>
          <p>Defense: {this.state.activePokemon.defense}</p>
		  <p>Element: {this.state.activePokemon.element}</p>
		  <p>Captured before: {(this.state.activePokemon.owned === true) ? "Yes" : "No"}</p>
          <Form>
            <FormGroup>
              <Label for="pokemon-name">Guess the number</Label>
              <Input
                type="number"
                id="number"
                name="number"
                onChange={this.handleChange}
                placeholder="Enter a number between 1 to 10"
              />
            </FormGroup>
          </Form>
		  <p>Results: {(this.state.match)? "Correct" : "Wrong"}</p>
        </ModalBody>
        <ModalFooter>
          <Button
            color="success"
            onClick={() => onSave(this.state.activePokemon)}
          >
            {word}
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
