import React, { Component } from "react";
import {
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Form,
  FormGroup,
  Input,
  Label,
} from "reactstrap";

export default class CustomModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activePokemon: this.props.activeItem,
    };
  }

  handleChange = (e) => {
    let { name, value } = e.target;

    if (e.target.type === "checkbox") {
      value = e.target.checked;
    }

    const activeItem = { ...this.state.activeItem, [name]: value };

    this.setState({ activeItem })
  };

  render() {
    const { toggle, onSave } = this.props;

    return (
      <Modal isOpen={true} toggle={toggle}>
        <ModalHeader toggle={toggle}>Pokemon Item</ModalHeader>
        <ModalBody>
          <Form>
            <FormGroup>
              <Label for="pokemon-name">Name</Label>
              <Input
                type="text"
                id="pokemon-name"
                name="name"
                value={this.state.activeItem.name}
                onChange={this.handleChange}
                placeholder="Enter Pokemon Name"
              />
            </FormGroup>
            <FormGroup>
              <Label for="pokemon-element">Element</Label>
              <Input
                type="text"
                id="pokemon-element"
                name="element"
                value={this.state.activeItem.element}
                onChange={this.handleChange}
                placeholder="Enter Pokemon Element"
              />
            </FormGroup>
            <FormGroup check>
              <Label check>
                <Input
                  type="checkbox"
                  name="owned"
                  checked={this.state.activePokemon.owned}
                  onChange={this.handleChange}
                />
                Owned
              </Label>
            </FormGroup>
          </Form>
        </ModalBody>
        <ModalFooter>
          <Button
            color="success"
            onClick={() => onSave(this.state.activeItem)}
          >
            Save
          </Button>
        </ModalFooter>
      </Modal>
    );
  }
}
